refentry.refsect1:
  &reftitle.description;

&style.procedural;

   stringmaxdb_get_server_info
   resourcelink


&style.oop;

   maxdb
   stringserver_info



   Returns a string representing the version of the MaxDB server that the
   MaxDB extension is connected to (represented by the
   link parameter).

  &reftitle.examples;

   &style.oop;


<?php
$maxdb = new maxdb("localhost", "MONA", "RED", "DEMODB");

/* check connection */
if (maxdb_connect_errno()) {
   printf("Connect failed: %s\n", maxdb_connect_error());
   exit();
}

/* print server version */
printf("Server version: %s\n", $maxdb->server_info);

/* close connection */
$maxdb->close();
?>




   &style.procedural;


<?php
$link = maxdb_connect("localhost", "MONA", "RED", "DEMODB");

/* check connection */
if (maxdb_connect_errno()) {
   printf("Connect failed: %s\n", maxdb_connect_error());
   exit();
}

/* print server version */
printf("Server version: %s\n", maxdb_get_server_info($link));

/* close connection */
maxdb_close($link);
?>



